import React from "react";
import './stylesheet.css';
import PartnerLogo1 from './images/section_images/partner_logo_1.png';
import { db, auth } from './firebase';
import { Link } from 'react-router-dom';
import restaurantTitle from './restaurantTitle';




class Restaurants extends React.Component {
    state = {
        restaurants: null,
        restaurantsID: null
    }

    componentDidMount() {
        console.log('mounted')
        db.collection('restaurants')
            .get()
            .then(snapshot => {
                const restaurants = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    restaurants.push(data)
                })
                const restaurantsID = []
                snapshot.forEach(doc => {
                    const dataID = doc.id
                    restaurantsID.push(dataID)
                })
                this.setState({ restaurants: restaurants })
                this.setState({ restaurantsID: restaurantsID })
                console.log(restaurants)
                console.log(restaurantsID)
            })
            .catch(error => console.log(error))
    }

    render() {

        return (
            <div className="App">
                {
                    this.state.restaurants &&
                    this.state.restaurants.map(restaurant, restaurantID => {
                        return (
                            <a>
                                <Link to={{
                                    pathname: `/seefood-app/restaurants/${restaurant.name}`,
                                    state: {
                                        name: `${restaurant.name}`,
                                        city: `${restaurant.city}`,
                                        state: `${restaurant.state}`,
                                        cuisine: `${restaurant.cuisine}`,
                                        street: `${restaurant.street}`,
                                        zip: `${restaurant.zip}`,
                                        phone: `${restaurant.phone}`,
                                        categories: `${restaurant.categories}`,
                                    }
                                }}>
                                    <div class="restaurant-box">
                                        <img src={PartnerLogo1} alt="" />
                                        <h3>{restaurant.name}</h3>
                                        <p>{restaurant.cuisine}</p>
                                        <p>SeeFood is a mobile website that helps you see pictures of food on the menu, instead of just reading
                                        about it.</p>
                                        <span>{restaurant.city}, {restaurant.state}</span>
                                    </div>
                                </Link>
                            </a>
                        )
                    })
                }
            </div >
        )
    }


}



// <Link to={{ pathname: '/route', state: { foo: 'bar'} }}>

// <Link to={`/seefood-app/restaurants/${restaurant.name}`} params={{ restaurants: `${restaurant.name}` }}></Link>


export default Restaurants;
