import React from "react";
import './stylesheet.css';
import { db, auth } from './firebase'



class RestaurantsTitle extends React.Component {
    state = {
        restaurants: null
    }

    componentDidMount() {
        console.log('mounted')
        db.collection('restaurants')
            .get()
            .then(snapshot => {
                const restaurants = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    restaurants.push(data)
                })
                const restaurantsID = []
                snapshot.forEach(doc => {
                    const dataID = doc.id()
                    restaurantsID.push(dataID)
                })
                this.setState({ restaurants: restaurants })
                // console.log(snapshot)
            })
            .catch(error => console.log(error))
    }

    render() {
        return (
            <div>
                {
                    this.state.restaurants &&
                    this.state.restaurants.map(restaurant => {
                        return (
                            <a>
                                <h2>{restaurants.name}</h2>
                                <p>SeeFood helps create visual menus for restaurants and customers.
                                    See your food before ordering!</p>
                                <ul class="button-container">
                                    <li><a href="#">Hours</a></li>
                                    <li><a href="#">Nutrition</a></li>
                                    <li><a href="#">PDF Menu</a></li>
                                </ul>
                            </a>
                        )
                    })
                }
            </div>
        )
    }
}


export default RestaurantsTitle;
