import { db, auth } from './firebase'




class Restaurants extends React.Component {
    state = {
        restaurants: null
    }


    render() {

        db.collection("restaurants")
            .get()
            .then(querySnapshot => {
                const data = querySnapshot.docs.map(doc => doc.data());
                const dataID = querySnapshot.docs.map(doc => doc.id);
                console.log(data);
                console.log(dataID); //STORE DOCUMENT ID's
            })

        return (
            <div className="App">
                {
                    this.state.restaurants &&
                    this.state.restaurants.map(restaurant => {
                        return (
                            <a href="/menu">
                                <div class="restaurant-box">
                                    <img src={PartnerLogo1} alt="" />
                                    <h3>{restaurant.name}</h3>
                                    <p>SeeFood is a mobile website that helps you see pictures of food on the menu, instead of just reading
                                    about it.</p>
                                    <span>{data.city}, {restaurant.state}</span>
                                </div>
                            </a>
                        )
                    })
                }
            </div>
        )
    }
}


export default Restaurants;
