import React from 'react';
import './stylesheet.css';
import PartnerLogo1 from "./images/section_images/partner_logo_1.png";
import { db, auth } from './firebase';



class MenuBox extends React.Component {
    state = {
        restaurants: null,
        restaurantsID: null
    }

    componentDidMount() {
        console.log('mounted')
        db.collection('restaurants').doc("9khcg5d96JQOdj06Vm4v").collection("categories")
            .get()
            .then(snapshot => {
                const restaurants = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    restaurants.push(data)
                })
                this.setState({ restaurants: restaurants })
            })
            .catch(error => console.log(error))
    }

    render() {
        return (
            <div>
                {
                    this.state.restaurants &&
                    this.state.restaurants.map(restaurant => {
                        return (
                            <a href="/sub-menu">
                                <div class="menu-category">
                                    <div class="menu-category-info">
                                        <h3>{`${restaurant.name}`}</h3>
                                        <p>{`${restaurant.categories[0].items[0].name}`}, {`${restaurant.categories[0].items[1].name}`}</p>
                                    </div>
                                </div>
                            </a>
                        )
                    })
                }
            </div >
        )
    }
}


export default MenuBox;
