import React from "react";
import './stylesheet.css';
import PartnerLogo1 from './images/section_images/partner_logo_1.png';
import { db, auth } from './firebase'
import { Link } from 'react-router-dom';




class Restaurants extends React.Component {
    state = {
        restaurants: null
    }

    componentDidMount() {
        console.log('mounted')
        db.collection('restaurants')
            .get()
            .then(snapshot => {
                const restaurants = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    restaurants.push(data)
                })
                const restaurantsID = []
                snapshot.forEach(doc => {
                    const dataID = doc.id
                    restaurantsID.push(dataID)
                })
                this.setState({ restaurants: restaurants })
                console.log(restaurants)
                console.log(restaurantsID)
            })
            .catch(error => console.log(error))

        firebasePhotos.once('value').then(snapshot => {
            const photos = firebaseLooper(snapshot);

            reverseArray(photos).map(photo => {
                this.getURL(photo.image)
            })

            this.setState({
                isLoading: false,
                marginTop: '0px',
            })
        });
    }

    getURL = filename => {
        //console.log(filename);
        db.storage()
            .ref('photos')
            .child(filename)
            .getDownloadURL()
            .then(url => {
                this.setState({ photos: [...this.state.photos, url] })
            });
    };

    render() {
        return (
            <div className="App">
                {
                    this.state.restaurants &&
                    this.state.restaurants.map(restaurant => {
                        return (
                            <a>
                                <Link to={`/restaurants/${restaurant.name}`}>
                                    <div class="restaurant-box">
                                        <img src={restaurant.logo} alt="" />
                                        <h3>{restaurant.name}</h3>
                                        <p>SeeFood is a mobile website that helps you see pictures of food on the menu, instead of just reading
                                        about it.</p>
                                        <span>{restaurant.city}, {restaurant.state}</span>
                                    </div>
                                </Link>
                            </a>
                        )
                    })
                }
            </div>
        )
    }
}


export default Restaurants;
